mapper.select:
SELECT id, email, password, nickname, create_time AS createTime
FROM sys_user
WHERE email = #{email}

SELECT COUNT(1)
FROM sys_user
WHERE email = #{email}

SELECT id, email, password, nickname, create_time AS createTime
FROM sys_user
WHERE id = #{id}

mapper.update:
UPDATE sys_user
SET nickname = #{nickname}
WHERE id = #{id}

UPDATE sys_user
SET avatar = #{avatar}
WHERE id = #{id}

UPDATE sys_user
nickname = #{nickname}, gender = #{gender}, birthday = #{birthday}, signature = #{signature}, update_time = NOW()
WHERE id = #{userId}
